import { useEffect, useState } from 'react'
import gql from "graphql-tag";
import { client } from '../components/Layout';
import Link from 'next/link';

import Layout from '../components/Layout';
import Loader from '../components/Loader';

const index = () => {

    const [rockets, setRockets] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        client
            .query({
                query: gql`
              query GET_ROCKETS {
                rockets {
                  id
                  name
                  description
                  country
                }
              }            
          `
            })
            .then(result => {
                console.log(result)
                setRockets(result.data.rockets)
                setIsLoading(false)
            });
    }, [])


    return (
        <div>
            {isLoading ?
                //isLoading (true)--------------------------------------------------------------------------------------------
                <Layout>
                    <div id="img">
                        <div style={{ backgroundColor: 'black', opacity: 0.8, height: '92vh' }}>
                            <Loader />
                        </div>
                    </div>
                </Layout>
                :
                //isLoading (false)--------------------------------------------------------------------------------------------
                <Layout>
                    <section class="section">
                        <div class="container">
                            <div class="columns is-multiline">
                                {rockets.map(rocket => (
                                    <div class="column">
                                        <div class="card" style={{ width: '300px' }}>
                                            <div class="card-image">
                                                <figure class="image is-4by3">
                                                    <img src={`/HomePic/${rocket.id}.jpg`} alt={rocket.id} />
                                                </figure>
                                            </div>
                                            <div class="card-content">
                                                <p class="title is-4">{rocket.name}</p>
                                                <Link href={`/rocket/${rocket.id}`}>
                                                    <p class="subtitle"><a>@{rocket.id}</a></p>
                                                </Link>

                                                <div class="content">
                                                    <p><b>Description: </b></p>
                                                    {rocket.description}
                                                </div>

                                                <div class="content">
                                                    <p><b>Country: </b></p>
                                                    {rocket.country}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </section>

                </Layout >
            }
            
            <style jsx>{`
            #img {
                background-image: url("/home_bgi.jpg");
                background-attachment: fixed;
                background-repeat: no-repeat;
                background-size: cover;
                min-height: 92vh;
            }
            `}</style>
        </div >
    );
};

export default index;
