import { useEffect, useState } from 'react';
import { useRouter } from 'next/router'
import gql from "graphql-tag";
import Head from 'next/head';

import Layout, { client } from '../../components/Layout';
import Loader from '../../components/Loader';

const Rocket = () => {

    const [rocket, setRocket] = useState({})
    const [histories, setHistories] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    // https://github.com/zeit/next.js/issues/8259
    const [id, setId] = useState(null)
    const { query } = useRouter()

    useEffect(() => {
        setId(query.id)

        if (id) {
            client
                .query({
                    query: gql`
                        query GET_ROCKET {
                            histories {
                                title
                                details
                                event_date_utc
                                flight {
                                  rocket {
                                    rocket {
                                      id
                                    }
                                  }
                                }
                              }
                              rocket(id: "${id}") {
                                id
                                name
                                description
                                country
                                diameter {
                                  feet
                                  meters
                                }
                                height {
                                  feet
                                  meters
                                }
                                mass {
                                  kg
                                  lb
                                }
                                payload_weights {
                                  kg
                                  lb
                                  name
                                }
                                first_stage {
                                  burn_time_sec
                                  engines
                                  fuel_amount_tons
                                  thrust_vacuum {
                                    kN
                                    lbf
                                  }
                                  thrust_sea_level {
                                    kN
                                    lbf
                                  }
                                }
                                second_stage {
                                  burn_time_sec
                                  engines
                                  thrust {
                                    kN
                                    lbf
                                  }
                                }
                                wikipedia
                              }
                        }
                    `
                })
                .then(result => {
                    // console.log(result)
                    setRocket(result.data.rocket)
                    setIsLoading(false)
                    setHistories(result.data.histories)
                });
        }
    }, [query, id])

    // Filter out histories don't have flight
    const filterHistory = histories.filter(function (history) {
        return (history.flight)
    });
    // console.log(filterHistory)

    // Filter out id not same with this page
    const rocketHistory = filterHistory.filter(function (history) {
        return (history.flight.rocket.rocket.id == `${id}`)
    });
    // console.log(rocketHistory)




    return (
        <div>
            {isLoading ?
                //isLoading (true)--------------------------------------------------------------------------------------------
                <Layout>
                    <div style={{ backgroundColor: 'black', opacity: 0.8, height: '92vh' }}>
                        <Loader />
                    </div>
                </Layout>
                :
                //isLoading (false)--------------------------------------------------------------------------------------------
                <Layout>
                    <Head>
                        <title>{rocket.name} | SpaceX</title>
                    </Head>
                    <p>RocketName: {rocket.name}</p>
                    <p>RocketDescription: {rocket.description}</p>
                    <p>RocketCountry: {rocket.country}</p>
                    <p>RocketWikipedia: {rocket.wikipedia}</p>
                    <br/>
                    <p>RocketDiameter: </p>
                    <p>Feet:{rocket.diameter.feet}</p>
                    <p>Meters:{rocket.diameter.meters}</p>
                    <br/>
                    <p>RocketHeight: </p>
                    <p>Feet:{rocket.height.feet}</p>
                    <p>Meters:{rocket.height.meters}</p>
                    <br/>
                    <p>RocketMass: </p>
                    <p>Kg:{rocket.mass.kg}</p>
                    <p>Lb:{rocket.mass.lb}</p>
                    <br/>
                    {rocket.payload_weights.map(payload => (
                        <div>
                            <p>PayloadName: {payload.name}</p>
                            <p>PayloadWeight: </p>
                            <p>Kg:{payload.kg}</p>
                            <p>Lb:{payload.lb}</p>
                        </div>
                    ))}
                    <br/>
                    <p>FirstStageEngines: {rocket.first_stage.engines}</p>
                    <p>FirstStageBurnTime: {rocket.first_stage.burn_time_sec}sec</p>
                    <p>FirstStageThrustVacuum: </p>
                    <p>Kn:{rocket.first_stage.thrust_vacuum.kN}</p>
                    <p>Lbf:{rocket.first_stage.thrust_vacuum.lbf}</p>
                    <p>FirstStageThrustSeaLevel: </p>
                    <p>Kn:{rocket.first_stage.thrust_sea_level.kN}</p>
                    <p>Lbf:{rocket.first_stage.thrust_sea_level.lbf}</p>
                    <br/>
                    <p>SecondStageEngines: {rocket.second_stage.engines}</p>
                    <p>SecondStageBurnTime: {rocket.second_stage.burn_time_sec}sec</p>
                    <p>SecondStageThrust: </p>
                    <p>Kn:{rocket.second_stage.thrust.kN}</p>
                    <p>Lbf:{rocket.second_stage.thrust.lbf}</p>

                    <br />
                    {rocketHistory.map(history => (
                        <div>
                            <p>HistoryTitle: {history.title}</p>
                            <p>HistoryDetails: {history.details}</p>
                            <p>HistoryDate: {history.event_date_utc}</p>
                        </div>
                    ))}

                </Layout>
            }
        </div>
    );
}

export default Rocket
