import { useEffect, useState } from 'react';
import { useRouter } from 'next/router'
import gql from "graphql-tag";
import { client } from '..';

const Rocket = () => {
    const [id, setId] = useState(null)
    const { query } = useRouter()
  
    useEffect(() => {
      setId(query.id)
  
      if (id) {
        console.log(id)
      }
    }, [query, id])

    return <p>Rocket: {id}</p>
}

export default Rocket
