import { useEffect, useState } from 'react'
import gql from "graphql-tag";
import { client } from '../components/Layout';
import Link from 'next/link';

import Layout from '../components/Layout';
import Loader from '../components/Loader';

const index = () => {

    const [rockets, setRockets] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        client
            .query({
                query: gql`
              query GET_ROCKETS {
                rockets {
                  id
                  name
                  description
                  country
                }
              }            
          `
            })
            .then(result => {
                console.log(result)
                setRockets(result.data.rockets)
                setIsLoading(false)
            });
    }, [])


    return (
        <div>
            {isLoading ?
                //isLoading (true)--------------------------------------------------------------------------------------------
                <Layout>
                    <div id="img">
                        <div style={{ backgroundColor: 'black', opacity: 0.8, height: '92vh' }}>
                            <Loader />
                        </div>
                    </div>
                </Layout>
                :
                //isLoading (false)--------------------------------------------------------------------------------------------
                <Layout>
                    <div id="img">
                        <div class="columns" style={{ backgroundColor: 'black', opacity: 0.8, height: '92vh', width: '100vw' }}>
                            {rockets.map(rocket => (
                                <div class="column" style={{ margin: '40px auto' }}>
                                    <div class="card">
                                        <div class="card-image">
                                            <figure class="image is-4by3">
                                                <img src="https://bulma.io/images/placeholders/1280x960.png" alt="Placeholder image" />
                                            </figure>
                                        </div>
                                        <div class="card-content">
                                            <div class="media">
                                                <div class="media-left">
                                                    <figure class="image is-48x48">
                                                        <img src="https://bulma.io/images/placeholders/96x96.png" alt="Placeholder image" />
                                                    </figure>
                                                </div>
                                                <div class="media-content">
                                                    <p class="title is-4">John Smith</p>
                                                    <p class="subtitle is-6">@johnsmith</p>
                                                </div>
                                            </div>

                                            <div class="content">
                                                Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Phasellus nec iaculis mauris. <a>@bulmaio</a>.
                            <a href="#">#css</a> <a href="#">#responsive</a>
                                                <br />
                                                <time datetime="2016-1-1">11:09 PM - 1 Jan 2016</time>
                                            </div>
                                        </div>
                                        <footer class="card-footer">
                                            <a href="#" class="card-footer-item">Save</a>
                                        </footer>
                                    </div>
                                </div>
                            ))
                            }
                        </div>
                    </div>
                </Layout>
            }

            <style jsx>{`
            #img {
                background-image: url("/home_bgi.jpg");
                background-attachment: fixed;
                background-repeat: no-repeat;
                background-size: cover;
                height: 92vh;
            }
            `}</style>
        </div >
    );
};

export default index;
