import Layout, { client } from "../components/Layout";
import { useEffect, useState } from 'react'
import gql from "graphql-tag";

const launches = () => {

    const [launches, setLaunches] = useState([])

    useEffect(() => {
        client
            .query({
                query: gql`
            query GET_LAUNCHES {
                launches {
                  mission_name
                  launch_date_local
                  launch_site {
                    site_name
                  }
                  launch_success
                  rocket {
                    rocket_name
                    rocket {
                      id
                    }
                  }
                }
              }          
        `
            })
            .then(result => {
                console.log(result)
                setLaunches(result.data.launches)
            });
    }, [])

    console.log(launches)

    return (
        <Layout>
            <section class="section">
                <div class="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <table class="table table is-striped is-fullwidth" >
                        <thead>
                            <tr>
                                <th>Mission</th>
                                <th></th>
                                <th>Launch Date</th>
                                <th>Mission Name</th>
                                <th>Launch Site</th>
                                <th>Rocket</th>
                            </tr>
                        </thead>
                        <tbody>
                            {launches.map(launch => (
                                <tr>
                                    {launch.launch_success == true
                                       ? <th style={{color: "green", textAlign:'center'}}>Success</th>
                                         : (launch.launch_success == false
                                            ? <th style={{color: "red", textAlign:'center'}}>Failed</th>
                                            : (launch.launch_success == null
                                                ? <th style={{textAlign:'center'}}>-</th>
                                                : null
                                            )
                                        )
                                    }
                                    <th>{launch.launch_success}</th>
                                    <td>{launch.launch_date_local}</td>
                                    <td>{launch.mission_name}</td>
                                    <td>{launch.launch_site.site_name}</td>
                                    <td>{launch.rocket.rocket_name}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        </Layout>
    );
}

export default launches;
