import { useEffect, useState } from 'react';
import { useRouter } from 'next/router'
import gql from "graphql-tag";
import Head from 'next/head';

import Layout, { client } from '../../components/Layout';
import Loader from '../../components/Loader';

const Rocket = () => {

    const [rocket, setRocket] = useState({})
    const [histories, setHistories] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    // https://github.com/zeit/next.js/issues/8259
    const [id, setId] = useState(null)
    const { query } = useRouter()

    useEffect(() => {
        setId(query.id)

        if (id) {
            client
                .query({
                    query: gql`
                        query GET_ROCKET {
                            histories {
                                title
                                details
                                event_date_utc
                                flight {
                                  rocket {
                                    rocket {
                                      id
                                    }
                                  }
                                }
                              }
                              rocket(id: "${id}") {
                                id
                                name
                                description
                                country
                                diameter {
                                  feet
                                  meters
                                }
                                height {
                                  feet
                                  meters
                                }
                                mass {
                                  kg
                                  lb
                                }
                                payload_weights {
                                  kg
                                  lb
                                  name
                                }
                                first_stage {
                                  burn_time_sec
                                  engines
                                  fuel_amount_tons
                                  thrust_vacuum {
                                    kN
                                    lbf
                                  }
                                  thrust_sea_level {
                                    kN
                                    lbf
                                  }
                                }
                                second_stage {
                                  burn_time_sec
                                  engines
                                  thrust {
                                    kN
                                    lbf
                                  }
                                }
                                wikipedia
                                stages
                              }
                        }
                    `
                })
                .then(result => {
                    // console.log(result)
                    setRocket(result.data.rocket)
                    setIsLoading(false)
                    setHistories(result.data.histories)
                });
        }
    }, [query, id])

    // Filter out histories don't have flight
    const filterHistory = histories.filter(function (history) {
        return (history.flight)
    });
    // console.log(filterHistory)

    // Filter out id not same with this page
    const rocketHistory = filterHistory.filter(function (history) {
        return (history.flight.rocket.rocket.id == `${id}`)
    });
    // console.log(rocketHistory)

    const changeNum = (x) => {
        return x.toLocaleString(x);
    }


    return (
        <div>
            {isLoading ?
                //isLoading (true)--------------------------------------------------------------------------------------------
                <Layout>
                    <div style={{ backgroundColor: 'black', opacity: 0.8, height: '92vh' }}>
                        <Loader />
                    </div>
                </Layout>
                :
                //isLoading (false)--------------------------------------------------------------------------------------------
                <Layout>
                    <Head>
                        <title>{rocket.name} | SpaceX</title>
                    </Head>
                    <p>RocketMass: </p>
                    <p>Kg:{rocket.mass.kg}</p>
                    <p>Lb:{rocket.mass.lb}</p>
                </Layout>
            }
        </div>
    );
}

export default Rocket
